import React, {Component} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Loader from 'react-loader-spinner';


function CurriculumList(props)
{
    return props.curriculum.map(c => {
        return (
            <div className="row">
                <div className="col-2">
                    {c.departmentCode}
                </div>
                <div className="col-2">
                    {c.courseCode}
                </div>
                <div className="col-3">
                    {c.name}
                </div>
                <div className="col">
                    {c.creditHours}
                </div>
            </div>
        )
    })
}

export default class ViewSummary extends Component 
{
    constructor(props)
    {
        super(props);
        this.handleInput = this.handleInput.bind(this);
        this.state = {
            curriculum: null,
            editMode: false
        }
    }

    componentDidMount()
    {
        axios.get('/curriculum/getCurriculum/' + localStorage.getItem("Cycle_Id"))
            .then(res => {
                console.log(res.data);
                this.setState({
                    curriculum: res.data.curriculum
                })
            })
    }

    handleInput(e)
    {
        this.setState({
            [e.target.name]: e.target.value
        })
    }


    render()
    {
        if (this.state.curriculum === null)
        {
            return <Loader 
                type="Oval"
                color="black"
                height="100"	
                width="100"
            />
        }
        else
        {
            return(
                <div className="col">
                    <h1>Cycle Curriculum</h1>
                    {this.state.curriculum.length > 0 ?
                        <>
                            <div className="row">
                                <div className="col-2">
                                    Department Code
                                </div>
                                <div className="col-2">
                                    Course Code
                                </div>
                                <div className="col-3">
                                    Course Name
                                </div>
                                <div className="col">
                                    Credit Hours
                                </div>
                            </div>
                            <CurriculumList curriculum={this.state.curriculum} />
                        </>
                    : <p>There is no curriculum associated with this cycle.</p> }

                    {this.state.editMode ?
                        <>

                        </>
                    : 
                        <button 
                            className="btn btn-primary"
                            name="editMode"
                            value="true" 
                            type="button"
                            onClick={this.handleInput}>
                            Edit Curriculum
                        </button>
                    }
                </div>
            )
        }
    }
}
