import React, {Component} from 'react';
import axios from "axios";

/**
 * props:
 *      assignmentId: the assignment to add the subjects to.
 */
export default class CreateAssignment extends Component
{
    constructor(props)
    {
        super(props);
        //this.updateManualStudentEntry = this.updateManualStudentEntry.bind(this);
        //this.addStudent = this.addStudent.bind(this);
        this.changeFile = this.changeFile.bind(this);
        this.fileInput = React.createRef();
        //this.onSubmit = this.onSubmit.bind(this);
        this.state = {
            assignmentId: props.assignmentId,
            showFileAlert: false,
            showAddButton: false,
            manualStudentEntry: []
        }
        console.log(this.state.assignmentId);
    }

    changeFile(e)
    {
        if (!(this.fileInput.current.files[0].type === "text/csv"))
        {
            this.setState({
                showFileAlert: true
            })
        }
        else
        {
            this.setState({
                showFileAlert: false,
                showAddButton: true
            })
        }
    }

    render()
    {
        return (
            <form>
                <div className="form-group">
                    <label>Add additional subjects from .csv file: </label>
                    <input type="file" className="form-control-file" ref={this.fileInput} onChange={this.changeFile} />
                    {this.state.showFileAlert ? <p className="text-danger">Invalid File</p>: null}
                </div>
                {this.state.showAddButton ? <input className="btn btn-sm btn-primary" type="submit" value="Add Subjects" /> : null}
            </form>
        )
    }
}
