import React, {Component} from 'react';
import './popup.css';

/**
 * Props:
 * -closePopup: a function to close the popup.
 * -rubrics: the list of rubrics to show in select form.
 * -submit: a function to save the changes to the 
 */

const Rubrics = props => {
  return props.rubrics.map(r => {
    return <option key={r.Rubric_Title} value={r.Rubric_Title}>{r.Rubric_Title}</option>
  })
}

export default class AddRubricMeasurePopup extends Component
{

    constructor(props)
    {
      super(props);
      this.handleDescriptionChange = this.handleDescriptionChange.bind(this);
      this.state = {
        rubric: props.rubrics[0],
        description: "Enter measure description.  This will displayed in Program Assessment Summary.",
        desiredScore: 0,
        desiredAcievement: 0
      }
    }

    handleRubricChange(e)
    {
        this.setState({
          rubirc: e.target.value
        })
    }

    handleDescriptionChange(e)
    {
        this.setState({
          description: e.target.value
        })
    }


    render() {
        return (
          <div className='popup'>
            <div className='popup_inner p-4'>
              <h1>Define Rubric Measure</h1>
              <form onSubmit={this.props.submit}>
                <div className="form-group">
                  <label>Select rubric: </label>
                  <select value={this.state.rubric} onChange={this.handleRubricChange} className="form-control">
                    <Rubrics rubrics={this.props.rubrics} />
                  </select>
                </div>
                <div className="form-group">
                  <label>Enter Measure Description: </label>
                  <textarea 
                    className="form-control" 
                    rows="7"
                    value={this.state.description}
                    onChange={this.handleDescriptionChange} 
                  />
                </div>
                <div className="form-group">
                  <label>Enter desired score: </label>
                  <input type="number" className="form-control" min="0" />
                </div>
                <div className="form-group">
                  <label>Enter percent to achieve score: </label>
                  <input type="number" className="form-control" min="0" max="100" />
                </div>
                <input type="submit" value="SUBMIT" />
              </form>
              <button className="btn btn-danger" onClick={this.props.closePopup}>Cancel</button>
            </div>
            <span className="mr-4">{this.state.rubric}</span>
            <span className="mr-4">{this.state.description}</span>
            <span className="mr-4">{this.state.desiredScore}</span>
            <span className="mr-4">{this.state.desiredAcievement}</span>
          </div>
        );
      }
}
