import React, {Component} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Loader from 'react-loader-spinner';
import uuid from 'uuid/v1';

function CurriculumList(props)
{
    return props.curriculum.map(c => {
        return (
            <div className="row">
                <div className="col-2">
                    {editMode ? 
                    <input 
                        type="text" 
                        id={c.courseId} 
                        maxLength="4" 
                        value={c.departmentCode}
                        name="departmentCode" 
                        onChange={props.changeCourse}
                        required />
                    : c.departmentCode}
                </div>
                <div className="col-2">
                    {editMode ?
                    <input 
                        type="number" 
                        id={c.courseId} 
                        min="0"
                        max="9999"
                        value={c.courseCode}
                        name="courseCode" 
                        onChange={props.changeCourse}
                        required />
                     : c.courseCode}
                </div>
                <div className="col-3">
                    {editMode ?
                    <input 
                        type="text" 
                        id={c.courseId} 
                        min="0"
                        max="9999"
                        value={c.name}
                        name="name" 
                        onChange={props.changeCourse}/>
                        : c.name}
                </div>
                <div className="col">
                    {editMode ?
                    <input 
                        type="number" 
                        id={c.courseId} 
                        min="0"
                        max="99"
                        value={c.creditHours}
                        name="creditHours" 
                        onChange={props.changeCourse}/>
                    : c.creditHours}
                </div>
            </div>
        )
    })
}

export default class ViewSummary extends Component 
{
    constructor(props)
    {
        super(props);
        this.handleInput = this.handleInput.bind(this);
        this.addCourse = this.addCourse.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.state = {
            curriculum: null,
            editMode: false,
            deletedIds: []
        }
    }

    componentDidMount()
    {
        axios.get('/curriculum/getCurriculum/' + localStorage.getItem("Cycle_Id"))
            .then(res => {
                console.log(res.data);
                this.setState({
                    curriculum: res.data.curriculum
                })
            })
    }

    handleInput(e)
    {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    addCourse()
    {
        let tempCurriculum = this.state.curriculum;
        let newId = uuid();
        tempCurriculum.push({
            deparmentCode: "",
            courseCode: "",
            creditHours: 0,
            name: "",
            courseId: newId
        })

        this.setState({
            curriculum: tempCurriculum
        })
    }

    deleteCourse()
    {

    }

    onSubmit(e)
    {
        e.preventDefault();
    }

    render()
    {
        if (this.state.curriculum === null)
        {
            return <Loader 
                type="Oval"
                color="black"
                height="100"	
                width="100"
            />
        }
        else
        {
            return(
                <div className="col">
                    <h1>Cycle Curriculum</h1>
                    <form onSubmit={this.onSubmit}>
                        {this.state.curriculum.length > 0 ?
                            <>
                                <div className="row">
                                    <div className="col-2">
                                        Department Code
                                    </div>
                                    <div className="col-2">
                                        Course Code
                                    </div>
                                    <div className="col-3">
                                        Course Name
                                    </div>
                                    <div className="col">
                                        Credit Hours
                                    </div>
                                </div>
                                <CurriculumList curriculum={this.state.curriculum} editMode={this.state.editMode} />
                            </>
                        : <p>There is no curriculum associated with this cycle.</p> }

                        {this.state.editMode ?
                            <>
                                <div className="mb-3">
                                    <button className="btn btn-secondary" onClick={this.addCourse}>Add Course</button>
                                </div>
                                <div>
                                    <input className="btn btn-success" type="submit" value="Save Changes" />
                                </div>
                            </>
                        : 
                            <button 
                                className="btn btn-primary"
                                name="editMode"
                                value="true" 
                                type="button"
                                onClick={this.handleInput}>
                                Edit Curriculum
                            </button>
                        }
                    </form>
                </div>
            )
        }
    }
}
