import React, {Component} from 'react';

//dummy data
var out1mea1 = {
    Measure_ID: 11,
    Description: 'out 1 measure 1'
}

var out1mea2 = {
    Measure_ID: 12,
    Description: 'out 1 measure 2'
}

var out1mea3 = {
    Measure_ID: 13,
    Description: 'out 1 measure 3'
}

var out1 = {
    Outcome_ID: 1,
    Description: 'outcome 1',
    measures: [out1mea1, out1mea2, out1mea3]
}

var out2mea1 = {
    Measure_ID: 21,
    Description: 'out 1 measure 1'
}

var out2mea2 = {
    Measure_ID: 22,
    Description: 'out 1 measure 2'
}

var out2mea3 = {
    Measure_ID: 23,
    Description: 'out 1 measure 3'
}

var out2 = {
    Outcome_ID: 2,
    Description: 'outcome 2',
    measures: [out2mea1, out2mea2, out2mea3]
}

var out3mea1 = {
    Measure_ID: 31,
    Description: 'out 1 measure 1'
}

var out3mea2 = {
    Measure_ID: 32,
    Description: 'out 1 measure 2'
}

var out3mea3 = {
    Measure_ID: 33,
    Description: 'out 1 measure 3'
}

var out3 = {
    Outcome_ID: 3,
    Description: 'outcome 3',
    measures: [out3mea1, out3mea2, out3mea3]
}

var dummyOutcomeList = [out1, out2, out3];

function SelectOutcome(props)
{
    return props.outcomeList.map((outcome, index) => {
        return <option key={outcome.Outcome_ID} value={index}>{outcome.Description}</option>
    })
}

function SelectMeasure(props)
{
    return props.measureList.map(measure => {
        return <option key={measure.Measure_ID} value={measure.Measure_ID}>{measure.Description}</option>
    })
}

export default class CreateAssignment extends Component
{
    constructor(props)
    {
        super(props);
        this.handleSelectOutcome = this.handleSelectOutcome.bind(this);
        this.handleSelectMeasure = this.handleSelectMeasure.bind(this);
        this.state = {
            outcomeList: dummyOutcomeList,
            evaluatorList: [],
            selectedOutcomeIndex: -1,
        }
    }

    handleSelectOutcome(e)
    {
        console.log("Before: " + this.state.selectedOutcomeIndex);
        this.setState({
            selectedOutcomeIndex: e.target.value
        })
        console.log("After: " + this.state.selectedOutcomeIndex);
    }

    handleSelectMeasure()
    {

    }

    render()
    {
        return(
            <form>
                <div className="form-group">
                    <label>Select Outcome: </label>
                    <select className="form-control" onChange={this.handleSelectOutcome}>
                        <SelectOutcome value={this.state.selectedOutcomeIndex} outcomeList={this.state.outcomeList} />
                    </select>
                </div>
                <div className="form-group">
                    <label>Select Measure: </label>
                    <select className="form-control" onChange={this.handleSelectMeasure}>
                        <SelectMeasure measureList={this.state.outcomeList[this.state.selectedOutcomeIndex].measures} />
                    </select>
                </div>
            </form>
        )
    }

}
