import React, {Component} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Loader from 'react-loader-spinner';


function CurriculumList(props)
{
    return props.curriculum.map(c => {
        return (
            <div className="row">
                <div className="col">
                    {c.departmentCode}
                </div>
                <div className="col">
                    {c.courseCode}
                </div>
                <div className="col">
                    {c.name}
                </div>
                <div className="col">
                    {c.departmentCode}
                </div>
                <div className="col">
                    {c.creditHours}
                </div>
            </div>
        )
    })
}

export default class ViewSummary extends Component 
{
    constructor(props)
    {
        super(props);
        this.state = {
            curriculum: null
        }
    }

    componentDidMount()
    {
        axios.get('/curriculum/getCurriculum/' + localStorage.getItem("Cycle_Id"))
            .then(res => {
                console.log(res.data);
                this.setState({
                    curriculum: res.data.curriculum
                })
            })
    }

    render()
    {
        if (this.state.curriculum === null)
        {
            return <Loader 
                type="Oval"
                color="black"
                height="100"	
                width="100"
            />
        }
        else
        {
            return(
                <>
                    {this.state.curriculum.length > 0 ?
                    <CurriculumList curriculum={this.state.curriculum} />
                    : <p>There is no curriculum associated with this cycle.</p> }

                    <button className="btn btn-primary">Add/Edit Curriculum</button>
                </>
            )
        }
    }
}
